const fname ='Laiba';
const lname ='Sajid';
const currentDate = new Date().toLocaleDateString();
const currentTime = new Date().toLocaleTimeString();
const img1 ='https://picsum.photos/200/300';
const img2 ='https://picsum.photos/250/300';
const img3 ='https://picsum.photos/290/300';
const links ='https://www.youtube.com/watch?v=OloAP8p1k1w&list=PLwGdqUZWnOp3aROg4wypcRhZqJG3ajZWJ&index=15';
// ********************************challenge no 3*********************
let curDate =new Date(2023, 2, 11, 18);
curDate = curDate.getHours();
let greeting = ' ';
if(curDate >= 1 && curDate <12){
greeting = ' Good Morning';

}else if (curDate >=12 && curDate <19){
  greeting = 'good Afternoon'
}else {
  greeting = 'Good Night';
}


const heading ={
    fontWeight: 'bold',
    color: '#f97306',
    textAlign: 'center',
    textTransform:'capitalize',
     margin: '20px 5px' ,
    textShadow:'0 2px 4px rgba(56, 55, 55, 0)' ,
    fontFamily: '"Poppins", sans-serif',
    fontSize: '50px'
}


const imgdiv = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around',


  }

  const imge = {
    width: '250px',
    height: '300px',
  }

