import React from "react";



const MenuCard = ({menueData}) =>{
    // console.log(menueData);
    return(
        <>
        
        {menueData.map((curElm) =>{
            return (
<>
                <div className="card-container">
                <div className="card">
                    <div className="card-body">
                        <span className="card-number card-circle subtle">1</span>
                    <span className="card-author subtle">Breakfast</span>
                    <h2 className="card-title">Maggi</h2>
                    <span className="card-description subtle">Maggie we all love it. its tasty yummy and easy to prepare. how about preparing ... Lockdown Special Maggi with wholesom</span>
                    <div className="card-read"> Read</div>
                   </div>
                    {/* <img src= {image} alt='images' className="card-media"/> */}
                    <span className="card-tag subtle">Order Now</span>
                    </div>
                </div>
                  </>
            )
        })
        }

        
        </>
    )
}

export default MenuCard;
