import React, { useState } from "react";
import "./style.css";
import Menu from "./menueApi";
import MenuCard from "./MenuCard";


const uniqueList = [
    ...new Set(
        Menu.map((curElm) => {
            return curElm.category;
        })
    ),
];





const Resturant = () => {
    const [menueData, setMenuData] = useState(Menu);
    const filterItem = (category) => {
        const updatedList = Menu.filter((curElm) => {
            return curElm.category === category;
        });
        setMenuData(updatedList);
    };
    // console.log(menueData);
    return (
        <>

            <nav className="navbar">
                <div className="btn-group">
                    <button className="btn-group__item" onClick={() => filterItem("BreakFast")}>
                        BreakFast
                    </button>

                    <button className="btn-group__item" onClick={() => filterItem("Lunch")}>
                        Lunch
                    </button>

                    <button className="btn-group__item" onClick={() => filterItem("Dinner")}>
                        Dinner
                    </button>

                    <button className="btn-group__item" onClick={() => setMenuData(Menu)}>
                        All
                    </button>

                </div>
            </nav>
            <MenuCard menueData={menueData} />

        </>

    );
};
export default Resturant;
